  end

  repeat
    pmid=0
    m:exec()
    if pmid==0 then
      return
    end

    local vPlay = nil
    local url=func[p[pmid].access](p[pmid].url)
    if url~=nil then
      if  vPlay  ==  nil  then
	vPlay  =  video.new()
      end

	local video_url,epg1,title = skyde(url)

	if epg1 == nil then
		epg1 = "Sky Sport stellt fÃ¼r diese Sendung keinen EPG-Text bereit."
	end

	if title == nil then
		title = p[pmid].title
	end

	if video_url then
		epg = conv_str(title) .. '\n\n' .. conv_str(epg1) 
		vPlay:setInfoFunc("epgInfo")
		vPlay:PlayFile("Sky Sport",video_url,conv_str(title)," " .. video_url);
	else
		print("Video URL not found")
	end

   end
  until false

end
function godirectkey(d)
	if d  == nil then return d end
	local  _dkey = ""
	if d == 1 then
		_dkey = RC.red
	elseif d == 2 then
		_dkey = RC.green
	elseif d == 3 then
		_dkey = RC.yellow
	elseif d == 4 then
		_dkey = RC.blue
	elseif d < 14 then
		_dkey = RC[""..d - 4 ..""]
	elseif d == 14 then
		_dkey = RC["0"]
	else
		-- rest
		_dkey = ""
	end
	return _dkey
end

function selectmenu()
	sm = menu.new{name="", icon=sky_sport}
	sm:addItem{type="separator"}
	sm:addItem{type="back"}
	sm:addItem{type="separatorline"}
	local d = 0 -- directkey
	for i,v in  ipairs(subs) do
		d = d + 1
		local dkey = godirectkey(d)
		sm:addItem{type="forwarder", name=v[2], action="fill_playlist",id=v[1], directkey=dkey }
	end
	sm:exec()
end

